INSERT INTO `article` VALUES ('17', '&nbsp;\r\n<div>\r\n	<p>\r\n		<span style=\"line-height:150%;font-size:16pt;\">csswzj.com</span><span style=\"line-height:150%;font-family:宋体;font-size:16pt;\">修改意见（</span><span style=\"line-height:150%;font-size:16pt;\">2011.5.25</span><span style=\"line-height:150%;font-family:宋体;font-size:16pt;\">）</span><span style=\"line-height:150%;font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"line-height:150%;font-family:宋体;font-size:16pt;\">陈、刘、廖</span><span style=\"line-height:150%;font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"line-height:150%;font-size:16pt;\">1</span><span style=\"line-height:150%;font-family:宋体;font-size:16pt;\">、网站名称：长沙市水利<span style=\"color:#ff6600;\">水电</span>工程质量监督站</span><span style=\"line-height:150%;font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"line-height:150%;font-size:16pt;\">2</span><span style=\"line-height:150%;font-family:宋体;font-size:16pt;\">、办事指南增加：</span><span style=\"line-height:150%;font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"line-height:150%;font-family:黑体;font-size:16pt;\">单元工程质量等级核备<span></span></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"line-height:150%;font-family:黑体;font-size:16pt;\">单位工程外观质量等级核定<span></span></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"line-height:150%;font-family:黑体;font-size:16pt;\">（原<span>5</span>个，增加后为七个办事栏目）<span></span></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"line-height:150%;font-family:黑体;font-size:16pt;\">增加后的排列顺序为：<span></span></span>\r\n	</p>\r\n	<p style=\"margin-left:21pt;\">\r\n		<span>3.1 </span><span style=\"font-family:黑体;\">水利工程质量监督手续；<span></span></span>\r\n	</p>\r\n	<p style=\"margin-left:21pt;\">\r\n		<span style=\"font-family:黑体;\">3.2</span><span style=\"font-family:黑体;\">工程项目划分确认；<span></span></span>\r\n	</p>\r\n	<p style=\"margin-left:21pt;\">\r\n		<span style=\"font-family:黑体;color:red;\">3.3</span><span style=\"font-family:黑体;color:red;\">单元工程质量等级核备；<span></span></span>\r\n	</p>\r\n	